mapper.select:
SELECT p.*, IF(s.id IS NULL, FALSE, TRUE) AS 'isStar', u.id AS 'createBy.id', IF(u.`name` is NULL,u.username,u.`name`) AS 'createBy.username', u.avatar AS 'createBy.avatar' from (SELECT * FROM project WHERE org_id IN
#{item.id}
) p LEFT JOIN `user` u ON u.id = p.user_id LEFT JOIN star s ON (s.target_id = p.id AND s.target = 'project' AND s.user_id = #{userId}) LEFT JOIN ( SELECT org.id, ruo.role, org.member_permission FROM rel_user_organization ruo LEFT JOIN organization org ON ruo.org_id = org.id WHERE ruo.user_id = #{userId} ) o ON o.id = p.org_id WHERE ( p.user_id = #{userId} OR p.visibility = 1 OR p.id IN ( SELECT p.id FROM project p LEFT JOIN rel_team_project rtp ON rtp.project_id = p.id LEFT JOIN team t ON t.id = rtp.team_id LEFT JOIN rel_user_team rut ON rut.team_id = t.id WHERE rut.user_id = #{userId} AND p.org_id IN
#{item.id}
) OR (o.member_permission > 0 AND p.visibility = 1) OR o.role > 0 )
AND LOWER(p.`name`) LIKE CONCAT(CONCAT('%', LOWER(#{keywords})), '%')
ORDER BY p.id
